import React from 'react';



class Login extends React.Component {


  render() {

let login = (
  <div className="card">
        <form class="form-inline" action='#'>
          <div class="form-group">
            <input type="password" class="form-control mx-sm-3" placeholder="Password"/>
            <button type="submit" class="btn btn-primary">Submit</button>
          </div>
        </form>
      </div>
);
  let contact =(
    <div className="card">
      <div className="top">
        <h2>{this.props.user.name}</h2>
        <img className='circle-img 'src={this.props.user.imgURL}/>
      </div>
      <div className="bottom">
        <h3>{this.props.user.phone}</h3> 
        <h3>{this.props.user.email}</h3>
      </div>
    </div>
  )
  let currComponent;
    if(this.props.user.authorize) {
      currComponent = contact
    } else {
      currComponent = <Login userName={this.setUserName}
      userpassword={this.setPassword} checkUser={this.usercheck}/>
    }
return (
  <div id="authorization">
    <h1>Enter the Password</h1>
    <div>{this.props.user.password ? login: contact }</div> 

  </div>
)
  }
}


export default Login;
